package javaoperator;

public class Assignmentoperator {
	/*
	=	a = b;	a = b;
	+=	a += b;	a = a + b;
	-=	a -= b;	a = a - b;
	*=	a *= b;	a = a * b;
	/=	a /= b;	a = a / b;
	%=	a %= b;	a = a % b;*/

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int a=30;
	int var;
	
	var=a;
	System.out.println(var);
	
	var += a;
	System.out.println(var);
	
	var-=a;
	System.out.println(var);
	
	var *= a;
	System.out.println(var);
	
	var /= a;
	System.out.println(var);
	}

}
